glsl3.vert:
precision highp float;
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec3 a_position;
uniform LAMYOUMG_COM {
  float textureWidth;
  float speed;
  float amplitude;
  float wave;
  vec2 startPos;
};
#if USE_TEXTURE
  in mediump vec2 a_uv0;
  out mediump vec2 v_uv0;
#endif
void main () {
  float angleSpanH = wave * 3.14159265;
  float pz = amplitude * sin(cc_time.x * speed - (a_position.x - startPos.x + a_position.y - startPos.y) / textureWidth * angleSpanH);
  vec4 position = vec4(a_position.x, a_position.y + pz, a_position.z, 1);
  #if USE_TEXTURE
    v_uv0 = a_uv0;
  #endif
  gl_Position = cc_matViewProj * cc_matWorld * position;
}

glsl3.frag:
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
precision highp float;
#if USE_TEXTURE
  in vec2 v_uv0;
  uniform sampler2D texture;
#endif
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    o *= texture(texture, v_uv0);
  #endif
  gl_FragColor = o;
}

glsl1.vert:
precision highp float;
uniform mat4 cc_matWorld;
uniform mat4 cc_matViewProj;
uniform vec4 cc_time;
attribute vec3 a_position;
uniform float textureWidth;
uniform float speed;
uniform float amplitude;
uniform float wave;
uniform vec2 startPos;
#if USE_TEXTURE
  attribute mediump vec2 a_uv0;
  varying mediump vec2 v_uv0;
#endif
void main () {
  float angleSpanH = wave * 3.14159265;
  float pz = amplitude * sin(cc_time.x * speed - (a_position.x - startPos.x + a_position.y - startPos.y) / textureWidth * angleSpanH);
  vec4 position = vec4(a_position.x, a_position.y + pz, a_position.z, 1);
  #if USE_TEXTURE
    v_uv0 = a_uv0;
  #endif
  gl_Position = cc_matViewProj * cc_matWorld * position;
}

glsl1.frag:
precision highp float;
#if USE_TEXTURE
  varying vec2 v_uv0;
  uniform sampler2D texture;
#endif
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    o *= texture2D(texture, v_uv0);
  #endif
  gl_FragColor = o;
}
